    height: 100% !important;
}

.menu__wrap [type="FUNCTION_WIDGET"]{
    background: white !important;
}

.menu{
    /* padding: 0 !important; */
}

.menu .bp3-button {
    justify-content: start;
    font-weight: 400 !important;
    font-size: 14px !important;
    box-shadow: none !important;
    border: none!important;
    transition: all 0.4s ease;
    color: rgba(125, 133, 146, 1);
}

.menu .bp3-button *{
    border: none!important;
}

.menu .bp3-button img{
    transition: all 0.4s ease;
    filter: brightness(0) saturate(100%) invert(57%) sepia(7%) saturate(636%) hue-rotate(178deg) brightness(91%) contrast(86%);
}

.menu .bp3-button.active img{
    transition: all 0.4s ease;
    filter: brightness(0) saturate(100%) invert(39%) sepia(99%) saturate(512%) hue-rotate(182deg) brightness(109%) contrast(104%);
}

.menu .bp3-button:not(.active):hover {
    border: 5px !important;
    border-radius: 10px; 
    transition: 0.5s;
}

.menu .bp3-button.active{
    color: rgba(63, 140, 255, 1);
    border-radius: 10px;
    background: rgba(63, 140, 255, 0.3) !important;  
}

.menu .bp3-button.active::after {
    content: '';
    display: block;
    position: absolute;
    left: 102%;
    top: 0;
    border-radius: 10px;
    width: 7px;
    height: 100%;
